import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [editingEvent, setEditingEvent] = useState(null);
  const [formData, setFormData] = useState({ title: '', description: '' });
  const [error, setError] = useState(null);  // To store error messages

  // Fetch events from the backend
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/events'); // Ensure API URL is correct
        setEvents(response.data);
      } catch (error) {
        setError('Error fetching events');
        console.error('Error fetching events:', error);
      }
    };
    fetchEvents();
  }, []);

  // Handle delete
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/events/${id}`);  // Ensure API URL is correct
      setEvents(events.filter(event => event.id !== id));
    } catch (error) {
      setError('Error deleting event');
      console.error('Error deleting event:', error);
    }
  };

  // Handle edit
  const handleEdit = (event) => {
    setEditingEvent(event);
    setFormData({ title: event.title, description: event.description });
  };

  // Handle update
  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:5000/api/events/${editingEvent.id}`, formData); // Ensure API URL is correct
      setEvents(events.map(event =>
        event.id === editingEvent.id ? { ...event, ...formData } : event
      ));
      setEditingEvent(null);  // Reset editing state
    } catch (error) {
      setError('Error updating event');
      console.error('Error updating event:', error);
    }
  };

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div>
      <h1>Events</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}  {/* Display error messages */}

      {editingEvent && (
        <div>
          <h2>Edit Event</h2>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            placeholder="Title"
          />
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Description"
          />
          <button onClick={handleUpdate}>Update</button>
        </div>
      )}

      <ul>
        {events.map(event => (
          <li key={event.id}>
            <h3>{event.title}</h3>
            <p>{event.description}</p>
            <button onClick={() => handleEdit(event)}>Edit</button>
            <button onClick={() => handleDelete(event.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Events;
