import { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Login() {
  const navigate = useNavigate();
  const { login: authLogin, logout, user: currentUser } = useContext(AuthContext);

  const [isRegistering, setIsRegistering] = useState(false);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [regName, setRegName] = useState("");
  const [regEmail, setRegEmail] = useState("");
  const [regPassword, setRegPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const [error, setError] = useState("");

  // ðŸ”´ FIX: redirect ONLY after login, not forcefully
  useEffect(() => {
    // do nothing here
  }, []);

  // ======================
  // LOGOUT
  // ======================
  const handleLogout = () => {
    logout();
    navigate("/login", { replace: true });
  };

  // ======================
  // LOGIN
  // ======================
  const handleLogin = async () => {
    setError("");

    if (!email || !password) {
      setError("Please enter email and password");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message || "Login failed");
        return;
      }

      authLogin(data.user);
      navigate("/products", { replace: true });
    } catch {
      setError("Server error");
    }
  };

  // ======================
  // REGISTER
  // ======================
  const handleRegister = async () => {
    setError("");

    if (!regName || !regEmail || !regPassword) {
      setError("Fill all fields");
      return;
    }

    if (regPassword !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/user/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: regName,
          email: regEmail,
          password: regPassword,
        }),
      });

      const data = await res.json();

      if (!res.ok) {
        setError(data.message || "Register failed");
        return;
      }

      authLogin(data.user);
      navigate("/products", { replace: true });
    } catch {
      setError("Server error");
    }
  };

  // ======================
  // UI
  // ======================
  return (
    <div style={{ minHeight: "80vh", display: "flex", justifyContent: "center", alignItems: "center" }}>
      <div style={{ width: 420, padding: 30, border: "1px solid #ddd" }}>
        <h2>{currentUser ? "Account" : isRegistering ? "Register" : "Login"}</h2>

        {error && <p style={{ color: "red" }}>{error}</p>}

        {/* ðŸ”´ LOGGED IN STATE */}
        {currentUser && (
          <>
            <p>Logged in as: <b>{currentUser.email}</b></p>
            <button
              onClick={handleLogout}
              style={{ width: "100%", padding: 10, cursor: "pointer" }}
            >
              Logout
            </button>
          </>
        )}

        {/* LOGIN */}
        {!currentUser && !isRegistering && (
          <>
            <input
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={{ width: "100%", padding: 10, marginBottom: 10 }}
            />

            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ width: "100%", padding: 10, marginBottom: 10 }}
            />

            <button
              onClick={handleLogin}
              style={{ width: "100%", padding: 10 }}
            >
              Login
            </button>

            <p style={{ marginTop: 10 }}>
              No account?{" "}
              <button onClick={() => setIsRegistering(true)}>Register</button>
            </p>
          </>
        )}

        {/* REGISTER */}
        {!currentUser && isRegistering && (
          <>
            <input
              placeholder="Full name"
              value={regName}
              onChange={(e) => setRegName(e.target.value)}
              style={{ width: "100%", padding: 10, marginBottom: 10 }}
            />

            <input
              placeholder="Email"
              value={regEmail}
              onChange={(e) => setRegEmail(e.target.value)}
              style={{ width: "100%", padding: 10, marginBottom: 10 }}
            />

            <input
              type="password"
              placeholder="Password"
              value={regPassword}
              onChange={(e) => setRegPassword(e.target.value)}
              style={{ width: "100%", padding: 10, marginBottom: 10 }}
            />

            <input
              type="password"
              placeholder="Confirm password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              style={{ width: "100%", padding: 10, marginBottom: 10 }}
            />

            <button
              onClick={handleRegister}
              style={{ width: "100%", padding: 10 }}
            >
              Register
            </button>

            <p style={{ marginTop: 10 }}>
              Have account?{" "}
              <button onClick={() => setIsRegistering(false)}>Login</button>
            </p>
          </>
        )}
      </div>
    </div>
  );
}
